sys_variable_value.value:
(function execute(inputs, outputs) {
    var util = new MSTeamsUtil();
    var mandatoryInputs = {
        group_id: 'Group ID / Team ID',
        channel_id: 'Channel ID',
        problem: 'Problem'
    };
    inputs = util.trimStringInputs(inputs);
    util.checkMandatoryInputs(inputs, mandatoryInputs);
    var hostedContent = {};
    if (inputs.inline_image_attachment)
        util.processAttachment(inputs.inline_image_attachment, hostedContent, inputs.inline_image_attachment_type);
    outputs.channel_id = inputs.channel_id;
    outputs.group_id = inputs.group_id;
    var payload = new MSTeamsGraphMessageBuilder();
    outputs.payload = payload.getPayload(inputs.fields, inputs.problem, inputs.title, inputs.additional_message, 'Problem', hostedContent, inputs.sharepoint_attachment);
})(inputs, outputs);

