sys_variable_value.value:
(function execute(inputs, outputs) {
    var responseBody = JSON.parse(inputs.responsebody);
    if (inputs.status == 201) {
        outputs.id = responseBody.id;
    } else {
        throw new Error(new MSTeamsErrorHandler().postReplyErrorHandler(inputs, outputs));
    }
})(inputs, outputs);

