sys_variable_value.value:
(function execute(inputs, outputs) {
    var util = new MSTeamsUtil();
    var mandatoryInputs = {
        team_id: 'Group ID / Team ID',
        channel_id: 'Channel ID',
        message_id: 'Message ID',
        message: 'Reply Message'
    };
    inputs = util.trimStringInputs(inputs);
    util.checkMandatoryInputs(inputs, mandatoryInputs);
    var obj = util.processMentions(inputs.mentions, inputs.message);
    inputs.mentions = obj.mentions;
    inputs.message = obj.message;
    var hostedContent = {};
    if (inputs.inline_image_attachment)
        util.processAttachment(inputs.inline_image_attachment, hostedContent, inputs.inline_image_attachment_type);
    var payload = new MSTeamsGraphMessageBuilder();
    outputs.message = payload.getPayloadForMSMessage("", inputs.message, hostedContent, inputs.sharepoint_attachment, inputs.mentions);
    outputs.channel_id = inputs.channel_id;
    outputs.team_id = inputs.team_id;
    outputs.message_id = inputs.message_id;
})(inputs, outputs);

